mapper.select:
select qe_article.article_id, qe_article.article_title, qe_article.article_content, qe_article.article_image, qe_article.section_id as article_section_id, qe_article.section_type_id, qe_article.author_id, qe_article.status, qe_article.article_weight, qe_article.article_top, qe_article.delete_flag as article_delete_flag, qe_article.create_time as article_create_time, qe_article.update_time as article_update_time, qe_section.section_id, qe_section.section_name, qe_section.section_introduce, qe_section.section_logo, qe_section.section_background, qe_section.section_admin_user_id, qe_section.section_weight, qe_section.section_top, qe_section.delete_flag, qe_section.create_time, qe_section.update_time, count(qal.get_like_qearticle_id) as like_count, COUNT(qc.collection_id) AS collection_count, count(q.comment_id) as comment_count from qe_article left join qe_section on qe_article.section_id = qe_section.section_id left join qe_collection as qc on qe_article.article_id = qc.article_id left join qe_comment as q on qe_article.article_id = q.article_id left join qe_article_like as qal on qe_article.article_id = qal.get_like_qearticle_id group by qe_article.article_id
qe_article.article_id = #{articleId}
and qe_article . article_title = #{articleTitle}
and qe_article . section_id = #{sectionId}
and qe_article . author_id = #{authorId}
and qe_article . article_top = #{articleTop}

mapper.delete:
delete
from qe_article
where author_id = #{param2}
  and article_id = #{param1}
